
public class IFstatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// This is to learn about if statements
		
		//if statement = performs a block code if it's condition evaluates to be true 
		// >,<,<=,>= are comparison operators in java if you want to check if something is equal use ==
		
		int age = 75;
		
		if(age == 75) {
			
			System.out.println("ok boomer");
		}
		
		else if (age>=18) {
			System.out.println("you are an adult");
			
			
		}
		
		else if (age>=13) {
			
			System.out.println("you are a teenager");
		}
		
		
		
		else {
			
			System.out.println("you are not an adult");
		}
		
		
		
		
		
		
		
		
		
	}

}
